package org.example.Data;import org.example.Data.BankAccs.BankAccount;import javax.persistence.*;import java.util.ArrayList;import java.util.List;@Entity@Table(name = "clients")public class Client {    @EmbeddedId    ClientKey key;    @OneToMany(mappedBy = "owner", cascade = CascadeType.ALL, orphanRemoval = true, fetch = FetchType.EAGER)    private List<BankAccount> accountsClient;    @Column    private String firstName;    @Column    private String surName;    public Client(){};    public Client(String firstName, String surName, String bank, long id) {        this.key = new ClientKey(id, bank);        this.firstName = firstName;        this.surName = surName;        accountsClient = new ArrayList<>();    }    public void addAccount(BankAccount account)  {        this.accountsClient.add(account);    }    public BankAccount getAccInTitle(String title) {        for (int i = 0; i < this.accountsClient.size(); ++i) {            if (this.accountsClient.get(i).getTitle().equals(title)) {                return this.accountsClient.get(i);            }        }        return null;    }    public void addAcc(BankAccount acc) {        this.accountsClient.add(acc);    }    public List<BankAccount> getAccs() {        return accountsClient;    }    public String getData() {        return this.surName + " " + this.firstName;    }    public String getFirstName() {        return firstName;    }    public void setFirstName(String firstName) {        this.firstName = firstName;    }    public String getSurName() {        return surName;    }    public void setSurName(String surName) {        this.surName = surName;    }    public Long getId() {        return key.getId();    }    public String getBank() {        return key.getBank();    }}