package org.example;import org.example.Data.BankAccs.*;import org.example.Data.Client;import org.example.Data.IdCounter;import org.hibernate.Session;import org.hibernate.SessionFactory;import org.hibernate.cfg.Configuration;import java.io.File;public class HibernateUtil {    private static final SessionFactory sessionFactory;    static {        File cfgFile = new File("hibernate.cfg.xml");        sessionFactory = new Configuration().configure(cfgFile)                .addAnnotatedClass(Client.class).addAnnotatedClass(BankAccount.class).addAnnotatedClass(DebitAcc.class).addAnnotatedClass(CreditAcc.class).addAnnotatedClass(DepositAcc.class).addAnnotatedClass(IdCounter.class).buildSessionFactory();    }    public static Session getSession(){        return sessionFactory.openSession();    }}